import { Component } from '@angular/core';

@Component({
  selector: 'app-home-restaraunts',
  templateUrl: './home-restaraunts.component.html',
  styleUrls: ['./home-restaraunts.component.scss']
})
export class HomeRestarauntsComponent {
  public restaraunts = [
    {
      title: 'Royal Sushi House',
      time: '30-40 min',
      img: 'assets/home/restaraunts/royal-suahi-house.png',
      cart: 10,
      price: '$32 min sum',  
      featured: 1, 
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Burgers & Pizza',
      time: '40-60 min',
      img: 'assets/home/restaraunts/burger-and-pizza.png',
      cart: 2,
      price: '$24 min sum', 
      featured: 1,     
      tags: [
        {
        title: 'Burger',
        img: 'assets/home/restaraunts/burger.png'
      },
      {
        title: 'Pizza',
        img: 'assets/home/restaraunts/pizza.png'
      }

    ]
    },
    {
      title: 'Ninja sushi',
      time: '20-40 min',
      img: 'assets/home/restaraunts/ninja-sushi.png',
      cart: 0,
      price: '$40 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Sushi master',
      time: '60-70 min',
      img: 'assets/home/restaraunts/sushi-master.png',
      cart: 0,
      price: '$49 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Japanese sushi',
      time: '30-50 min',
      img: 'assets/home/restaraunts/japanese-sushi.png',
      cart: 0,
      price: '$104 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Kobe',
      time: '20-30 min',
      img: 'assets/home/restaraunts/kobe.png',
      cart: 3,
      price: '$57 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    }

  ]
}
<div class="restaraunts">
  <div class="restaraunts__container">
    <div class="restaraunts__title">Nearby restaurants</div>
    <div class="restaraunts__list">
      <div class="restaraunts-item" *ngFor="let item of restaraunts">
        <div class="restaraunts-item__img">
          <img [src]="item.img" [alt]="item.title" />
        </div>
        <div class="restaraunts-item__info">
          <div class="restaraunts-item__title">{{ item.title }}</div>

          <div class="restaraunts-item__time-price">
            <div class="restaraunts-item__clock"><img src="assets/home/restaraunts/clock.png" alt="clock"></div>
            <div class="restaraunts-item__time">{{ item.time }}</div>
            <div class="restaraunts-item__dot"><img src="assets/home/restaraunts/dot.png" alt="dot"></div>
            <div class="restaraunts-item__price">{{ item.price }}</div>
          </div>


          <div class="restaraunts-item__tags">
            <div class="restaraunts-item__tag" *ngFor="let tag of item.tags">
              <img class="restaraunts-item__tag-icon" [src]="tag.img" [alt]="tag.title" />
              <div class="restaraunts-item__tag-title">{{ tag.title }}</div>
            </div>
          </div>


          <div class="restaraunts-item__cart-icon"><svg [class.is-active]="item.cart" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none">
            <path d="M5 1.66667L2.5 5.00001V16.6667C2.5 17.1087 2.67559 17.5326 2.98816 17.8452C3.30072 18.1577 3.72464 18.3333 4.16667 18.3333H15.8333C16.2754 18.3333 16.6993 18.1577 17.0118 17.8452C17.3244 17.5326 17.5 17.1087 17.5 16.6667V5.00001L15 1.66667H5Z" stroke="#C7C8D2" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
            <path d="M2.5 5H17.5" stroke="#C7C8D2" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
            <path d="M13.3333 8.33333C13.3333 9.21738 12.9821 10.0652 12.357 10.6904C11.7319 11.3155 10.8841 11.6667 10 11.6667C9.11595 11.6667 8.2681 11.3155 7.64298 10.6904C7.01786 10.0652 6.66667 9.21738 6.66667 8.33333" stroke="#C7C8D2" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
          </svg><!--<img src="assets/home/restaraunts/cart.png" alt="cart">--></div>
          <div class="restaraunts-item__cart-qty" *ngIf="item.cart"><span>{{ item.cart }}</span></div>

          <!--<div *ngIf="item.cart">Active</div>{{ item.cart }}</div>-->
        </div>

         <div class="restaraunts-item__featured" *ngIf="item.featured">FEATURED</div>
      </div>
    </div>
  </div>
</div>
