import { Component } from '@angular/core';

@Component({
  selector: 'app-home-restaraunts',
  templateUrl: './home-restaraunts.component.html',
  styleUrls: ['./home-restaraunts.component.scss']
})
export class HomeRestarauntsComponent {
  public restaraunts = [
    {
      title: 'Royal Sushi House',
      time: '30-40 min',
      img: 'assets/home/restaraunts/royal-suahi-house.png',
      cart: 4,
      price: '$32 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Burgers & Pizza',
      time: '40-60 min',
      img: 'assets/home/restaraunts/burger-and-pizza.png',
      cart: 2,
      price: '$24 min sum',     
      tags: [
        {
        title: 'Burger',
        img: 'assets/home/restaraunts/burger.png'
      },
      {
        title: 'Pizza',
        img: 'assets/home/restaraunts/pizza.png'
      }

    ]
    },
    {
      title: 'Ninja sushi',
      time: '20-40 min',
      img: 'assets/home/restaraunts/ninja-sushi.png',
      cart: 0,
      price: '$40 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Sushi master',
      time: '60-70 min',
      img: 'assets/home/restaraunts/sushi-master.png',
      cart: 0,
      price: '$49 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Japanese sushi',
      time: '30-50 min',
      img: 'assets/home/restaraunts/japanese-sushi.png',
      cart: 0,
      price: '$104 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    },
    {
      title: 'Kobe',
      time: '20-30 min',
      img: 'assets/home/restaraunts/kobe.png',
      cart: 0,
      price: '$57 min sum',     
      tags: [
        {
        title: 'Sushi',
        img: 'assets/home/restaraunts/sushi.png'
      }
    ]
    }

  ]
}
<div class="restaraunts">
  <div class="restaraunts__container">
    <div class="restaraunts__title">Nearby restaurants</div>
    <div class="restaraunts__list">
      <div class="restaraunts-item" *ngFor="let item of restaraunts">
        <div class="restaraunts-item__img">
          <img [src]="item.img" [alt]="item.title" />
        </div>
        <div class="restaraunts-item__title">{{ item.title }}</div>
        <div class="restaraunts-item__time">{{ item.time }}</div>
        <div class="restaraunts-item__price">{{ item.price }}</div>
        <div class="restaraunts-item__tags">
          <div class="restaraunts-item__tag" *ngFor="let tag of item.tags">
            <img [src]="tag.img" [alt]="tag.title" />
            {{ tag.title }}
          </div>
        </div>
        <div class="restaraunts-item__cart">
          Cart
          <div *ngIf="item.cart">{{ item.cart }}</div>
        </div>
        <!--<div *ngIf="item.cart">Active</div>{{ item.cart }}</div>-->
      </div>
    </div>
  </div>
</div>
